import React, { useEffect, useMemo, useState } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { getListeningLessons, generateTTS } from '../utils/api';
import './ListeningPractice.css';

const ToggleButton = ({ active, onClick, children }) => (
  <button
    className={`primary-btn ${active ? 'primary-btn-active' : ''}`}
    onClick={onClick}
    type="button"
  >
    {children}
  </button>
);

const TTSInputBox = ({
  value,
  onChange,
  onSubmit,
  loading,
  error,
  placeholder,
}) => (
  <div className="tts-container">
    <textarea
      value={value}
      onChange={(e) => onChange(e.target.value)}
      placeholder={placeholder}
      className="tts-textarea"
    />
    {error && <div className="tts-error">{error}</div>}
    <button
      className="primary-btn w-full"
      onClick={onSubmit}
      disabled={loading}
      type="button"
    >
      {loading ? 'Converting...' : 'Convert to Audio'}
    </button>
  </div>
);

const TTSQuestionList = ({ mcqs, responses, onSelect }) => {
  if (!mcqs.length) return null;

  return (
    <div className="mcq-list">
      <h4 className="mcq-title">Quick Quiz (5 MCQs)</h4>
      {mcqs.map((mcq, idx) => (
        <div key={idx} className="mcq-card">
          <div className="mcq-question">
            {idx + 1}. {mcq.question}
          </div>
          <div className="mcq-options">
            {mcq.options.map((opt, optIdx) => {
              const selected = responses[idx];
              const isCorrect = selected === opt && opt === mcq.answer;
              const isWrong = selected === opt && opt !== mcq.answer;
              return (
                <button
                  key={optIdx}
                  className={`mcq-option ${isCorrect ? 'correct' : ''} ${isWrong ? 'wrong' : ''}`}
                  onClick={() => onSelect(idx, opt)}
                  type="button"
                >
                  {opt}
                </button>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
};

const ListeningPractice = () => {
  const [searchParams] = useSearchParams();
  const selectedLanguage = searchParams.get('lang') || 'English';
  const languageCode = searchParams.get('code') || 'en-US';

  const [lessons, setLessons] = useState([]);
  const [filteredLessons, setFilteredLessons] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [ttsText, setTtsText] = useState('');
  const [ttsAudio, setTtsAudio] = useState('');
  const [ttsLoading, setTtsLoading] = useState(false);
  const [ttsError, setTtsError] = useState('');
  const [showTTS, setShowTTS] = useState(languageCode !== 'en-US');
  const [mcqs, setMcqs] = useState([]);
  const [mcqResponses, setMcqResponses] = useState({});

  const isEnglish = languageCode === 'en-US';

  const categories = ['All', 'Story', 'Conversation', 'Podcast'];

  const placeholder = 'Type or paste text to convert into audio...';

  const keywordPool = useMemo(() => {
    const words = ttsText
      .split(/\W+/)
      .filter((w) => w.length > 4)
      .map((w) => w.toLowerCase());
    return Array.from(new Set(words));
  }, [ttsText]);

  const generateMCQsFromText = (text) => {
    const baseWords = keywordPool.length ? keywordPool : ['learning', 'practice', 'language', 'audio', 'speech'];
    const fillers = ['context', 'dialogue', 'summary', 'phrase', 'accent', 'tone', 'grammar', 'vocabulary'];
    const getRandomFillers = (count, exclude) => {
      const pool = fillers.filter((f) => f !== exclude);
      const shuffled = [...pool].sort(() => 0.5 - Math.random());
      return shuffled.slice(0, count);
    };

    const mcqList = [];
    const slice = baseWords.slice(0, 5);

    while (mcqList.length < 5) {
      const word = slice[mcqList.length] || fillers[mcqList.length % fillers.length];
      const correct = word;
      const opts = [correct, ...getRandomFillers(3, correct)];
      const shuffled = opts.sort(() => 0.5 - Math.random());
      mcqList.push({
        question: `Which word best relates to the generated audio content? (${word})`,
        options: shuffled,
        answer: correct,
      });
    }

    return mcqList;
  };

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const data = await getListeningLessons();
        setLessons(data);
        setFilteredLessons(data);
      } catch (err) {
        setError('Unable to load listening lessons. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (isEnglish) {
      fetchLessons();
    } else {
      setLessons([]);
      setFilteredLessons([]);
      setError('');
      setLoading(false);
    }
  }, [isEnglish]);

  useEffect(() => {
    setShowTTS(languageCode !== 'en-US');
    setTtsText('');
    setTtsAudio('');
    setTtsError('');
    setMcqs([]);
    setMcqResponses({});
  }, [languageCode]);

  useEffect(() => {
    if (selectedCategory === 'All') {
      setFilteredLessons(lessons);
    } else {
      setFilteredLessons(lessons.filter(lesson => lesson.category === selectedCategory));
    }
  }, [selectedCategory, lessons]);

  const handleGenerateTTS = async () => {
    if (!ttsText.trim()) {
      setTtsError('Please enter text to convert.');
      return;
    }

    setTtsError('');
    setTtsAudio('');
    setTtsLoading(true);

    try {
      const data = await generateTTS(ttsText, languageCode);
      const audioBase64 = data.audio || data.base64 || data;
      setTtsAudio(`data:audio/mp3;base64,${audioBase64}`);
      setMcqs(generateMCQsFromText(ttsText));
      setMcqResponses({});
    } catch (err) {
      setTtsError('Unable to generate audio. Please try again.');
    } finally {
      setTtsLoading(false);
    }
  };

  const handleSelectOption = (questionIdx, option) => {
    setMcqResponses((prev) => ({ ...prev, [questionIdx]: option }));
  };

  const renderTTSSection = () => (
    <div className="tts-section">
      <TTSInputBox
        value={ttsText}
        onChange={setTtsText}
        onSubmit={handleGenerateTTS}
        loading={ttsLoading}
        error={ttsError}
        placeholder={placeholder}
      />
      {ttsAudio && (
        <div className="tts-audio-player">
          <audio controls src={ttsAudio}>
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
      <TTSQuestionList
        mcqs={mcqs}
        responses={mcqResponses}
        onSelect={handleSelectOption}
      />
    </div>
  );

  if (loading) {
    return <div className="listening-loading">Loading listening lessons...</div>;
  }

  if (error) {
    return <div className="listening-error">{error}</div>;
  }

  return (
    <div className="listening-page">
      <div className="listening-header">
        <h1>Listening Practice</h1>
        <p>
          {isEnglish 
            ? 'Select a lesson to practice your listening skills or switch to Text to Speech mode.'
            : `Convert your own text to audio in ${selectedLanguage}.`}
        </p>
        <p className="language-badge">Language: <strong>{selectedLanguage}</strong></p>
      </div>

      {isEnglish && (
        <div className="tts-toggle">
          <ToggleButton active={showTTS} onClick={() => setShowTTS((prev) => !prev)}>
            Text to Speech Mode
          </ToggleButton>
        </div>
      )}

      {isEnglish && !showTTS && (
        <>
          <div className="category-filters">
            {categories.map((category) => (
              <button
                key={category}
                className={`category-filter-btn ${selectedCategory === category ? 'active' : ''}`}
                onClick={() => setSelectedCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="listening-list grid-list">
            {filteredLessons.map((lesson) => (
              <div key={lesson._id} className="listening-card">
                <div>
                  <h3>{lesson.title}</h3>
                  <p>
                    {lesson.category && <span className="lesson-category">{lesson.category}</span>}
                    {lesson.category && ' • '}
                    {lesson.level || 'Beginner'} • {lesson.duration ? `${lesson.duration}s` : 'Duration TBD'}
                  </p>
                </div>
                <div className="listening-actions">
                  {isEnglish && (
                    <audio controls src={lesson.audio}>
                      Your browser does not support the audio element.
                    </audio>
                  )}
                  <Link 
                    to={`/listening-practice/${lesson._id}?lang=${encodeURIComponent(selectedLanguage)}&code=${encodeURIComponent(languageCode)}`} 
                    className="listening-link"
                  >
                    View Details
                  </Link>
                </div>
              </div>
            ))}
            {filteredLessons.length === 0 && (
              <div className="listening-empty">No listening lessons available in this category.</div>
            )}
          </div>
        </>
      )}

      {(!isEnglish || showTTS) && renderTTSSection()}
    </div>
  );
};

export default ListeningPractice;
