import React, { useEffect, useState, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import { getListeningLesson } from '../utils/api';
import './ListeningLesson.css';

const ListeningLesson = () => {
  const { id } = useParams();
  const [lesson, setLesson] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [playbackRate, setPlaybackRate] = useState(1);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizFeedback, setQuizFeedback] = useState({});
  const audioRef = useRef(null);

  const speedOptions = [0.5, 1, 1.5, 2];

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const data = await getListeningLesson(id);
        setLesson(data);
      } catch (err) {
        setError('Unable to load this listening lesson.');
      } finally {
        setLoading(false);
      }
    };

    fetchLesson();
  }, [id]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.playbackRate = playbackRate;
    }
  }, [playbackRate]);

  const handleSpeedChange = (speed) => {
    setPlaybackRate(speed);
  };

  const handleQuizAnswer = (questionIndex, selectedAnswer) => {
    if (quizAnswers[questionIndex] !== undefined) {
      return; // Already answered
    }

    const isCorrect = selectedAnswer === lesson.quiz[questionIndex].answer;
    setQuizAnswers({ ...quizAnswers, [questionIndex]: selectedAnswer });
    setQuizFeedback({ ...quizFeedback, [questionIndex]: isCorrect });
  };

  if (loading) {
    return <div className="lesson-loading">Loading lesson...</div>;
  }

  if (error) {
    return <div className="lesson-error">{error}</div>;
  }

  if (!lesson) {
    return <div className="lesson-error">Lesson not found.</div>;
  }

  return (
    <div className="listening-lesson-page">
      <div className="lesson-header">
        <h1>{lesson.title}</h1>
        <p>
          Level: <strong>{lesson.level}</strong> • Duration:{' '}
          {lesson.duration ? `${lesson.duration}s` : 'TBD'}
        </p>
      </div>

      <div className="lesson-audio">
        <audio controls src={lesson.audio}>
          Your browser does not support the audio element.
        </audio>
      </div>

      <div className="lesson-transcript">
        <h2>Transcript</h2>
        <p>{lesson.transcript}</p>
      </div>

      <Link to="/listening-practice" className="lesson-back-link">
        ← Back to all listening lessons
      </Link>
    </div>
  );
};

export default ListeningLesson;
