import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getListeningLessons } from '../utils/api';
import './ListeningPractice.css';

const ListeningPractice = () => {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const data = await getListeningLessons();
        setLessons(data);
      } catch (err) {
        setError('Unable to load listening lessons. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  if (loading) {
    return <div className="listening-loading">Loading listening lessons...</div>;
  }

  if (error) {
    return <div className="listening-error">{error}</div>;
  }

  return (
    <div className="listening-page">
      <div className="listening-header">
        <h1>Listening Practice</h1>
        <p>Select a lesson to practice your listening skills.</p>
      </div>

      <div className="listening-list">
        {lessons.map((lesson) => (
          <div key={lesson._id} className="listening-card">
            <div>
              <h3>{lesson.title}</h3>
              <p>{lesson.level || 'Beginner'} â€¢ {lesson.duration ? `${lesson.duration}s` : 'Duration TBD'}</p>
            </div>
            <div className="listening-actions">
              <audio controls src={lesson.audio}>
                Your browser does not support the audio element.
              </audio>
              <Link to={`/listening-practice/${lesson._id}`} className="listening-link">
                View Details
              </Link>
            </div>
          </div>
        ))}
        {lessons.length === 0 && (
          <div className="listening-empty">No listening lessons available yet.</div>
        )}
      </div>
    </div>
  );
};

export default ListeningPractice;
