import React from 'react';
import './TranslationBox.css';

const TranslationBox = ({ onTranslate }) => {
  if (!onTranslate) return null;

  return (
    <div className="info-card">
      <h4 className="info-title">Translations</h4>
      <div className="translation-actions">
        <button type="button" className="primary-btn" onClick={() => onTranslate('hi')}>
          Translate to Hindi
        </button>
        <button type="button" className="primary-btn" onClick={() => onTranslate('en')}>
          Translate to English
        </button>
        <button type="button" className="primary-btn" onClick={() => onTranslate('es')}>
          Translate to Spanish
        </button>
      </div>
    </div>
  );
};

export default TranslationBox;
