import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { getListeningLessons, getAssetUrl } from '../utils/api';
import './ListeningPractice.css';

const formatDuration = (seconds) => {
  if (seconds === null || seconds === undefined) {
    return 'Duration: N/A';
  }

  const mins = Math.floor(seconds / 60);
  const secs = Math.max(0, seconds % 60);
  return `Duration: ${mins}m ${secs.toString().padStart(2, '0')}s`;
};

const ListeningPractice = () => {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const data = await getListeningLessons();
        setLessons(data);
      } catch (err) {
        setError('Unable to load listening lessons right now.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchLessons();
  }, []);

  return (
    <div className="listening-page">
      <Header />
      <main className="listening-content">
        <div className="listening-hero">
          <div>
            <p className="listening-eyebrow">Listening Practice</p>
            <h1>Sharpen your ear with guided lessons</h1>
            <p className="listening-subtitle">
              Press play, read along with the transcript, and level up your comprehension.
            </p>
          </div>
        </div>

        {loading && <div className="listening-state">Loading lessons...</div>}
        {error && !loading && <div className="listening-state error">{error}</div>}
        {!loading && !error && lessons.length === 0 && (
          <div className="listening-state">No listening lessons yet. Add audio files to get started.</div>
        )}

        <div className="listening-list">
          {lessons.map((lesson) => (
            <Link
              key={lesson._id}
              to={`/listening-practice/${lesson._id}`}
              className="listening-card"
            >
              <div className="listening-card__content">
                <div>
                  <p className="listening-level">{lesson.level}</p>
                  <h3>{lesson.title}</h3>
                  <p className="listening-duration">{formatDuration(lesson.duration)}</p>
                </div>
                <audio controls preload="none" src={getAssetUrl(lesson.audio)}>
                  Your browser does not support the audio element.
                </audio>
              </div>
            </Link>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default ListeningPractice;
