import React, { useEffect, useState, useRef } from 'react';
import { useParams, Link, useSearchParams } from 'react-router-dom';
import { getListeningLesson, generateTTS } from '../utils/api';
import './ListeningLesson.css';

const ListeningLesson = () => {
  const { id } = useParams();
  const [searchParams] = useSearchParams();
  const selectedLanguage = searchParams.get('lang') || 'English';
  const languageCode = searchParams.get('code') || 'en-US';

  const [lesson, setLesson] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [playbackRate, setPlaybackRate] = useState(1);
  const [quizAnswers, setQuizAnswers] = useState({});
  const [quizFeedback, setQuizFeedback] = useState({});
  const [generatedAudioUrl, setGeneratedAudioUrl] = useState(null);
  const [isGeneratingAudio, setIsGeneratingAudio] = useState(false);
  const audioRef = useRef(null);
  const ttsAudioRef = useRef(null);

  const speedOptions = [0.5, 1, 1.5, 2];
  const isEnglish = selectedLanguage === 'English';

  useEffect(() => {
    const fetchLesson = async () => {
      try {
        const data = await getListeningLesson(id);
        setLesson(data);
      } catch (err) {
        setError('Unable to load this listening lesson.');
      } finally {
        setLoading(false);
      }
    };

    fetchLesson();
  }, [id]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.playbackRate = playbackRate;
    }
  }, [playbackRate]);

  const handleSpeedChange = (speed) => {
    setPlaybackRate(speed);
  };

  const handleQuizAnswer = (questionIndex, selectedAnswer) => {
    if (quizAnswers[questionIndex] !== undefined) {
      return; // Already answered
    }

    const isCorrect = selectedAnswer === lesson.quiz[questionIndex].answer;
    setQuizAnswers({ ...quizAnswers, [questionIndex]: selectedAnswer });
    setQuizFeedback({ ...quizFeedback, [questionIndex]: isCorrect });
  };

  const generateAudio = async () => {
    if (!lesson || !lesson.transcript) {
      return;
    }

    setIsGeneratingAudio(true);
    try {
      const response = await generateTTS(lesson.transcript, languageCode);

      // Convert base64 to blob URL
      const audioBase64 = response.audio;
      const binaryString = atob(audioBase64);
      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }
      const blob = new Blob([bytes], { type: 'audio/mp3' });
      const audioUrl = URL.createObjectURL(blob);

      setGeneratedAudioUrl(audioUrl);

      // Auto-play the audio
      if (ttsAudioRef.current) {
        ttsAudioRef.current.load();
        ttsAudioRef.current.play().catch(err => {
          console.error('Error playing audio:', err);
        });
      }
    } catch (err) {
      console.error('Error generating TTS audio:', err);
      setError('Failed to generate audio. Please try again.');
    } finally {
      setIsGeneratingAudio(false);
    }
  };

  if (loading) {
    return <div className="lesson-loading">Loading lesson...</div>;
  }

  if (error) {
    return <div className="lesson-error">{error}</div>;
  }

  if (!lesson) {
    return <div className="lesson-error">Lesson not found.</div>;
  }

  return (
    <div className="listening-lesson-page">
      <div className="lesson-header">
        <h1>{lesson.title}</h1>
        <p>
          {lesson.category && (
            <>
              Category: <strong>{lesson.category}</strong> ‚Ä¢{' '}
            </>
          )}
          Level: <strong>{lesson.level}</strong> ‚Ä¢ Duration:{' '}
          {lesson.duration ? `${lesson.duration}s` : 'TBD'}
        </p>
        {!isEnglish && (
          <p className="language-badge">Language: <strong>{selectedLanguage}</strong></p>
        )}
      </div>

      {isEnglish && (
        <>
          <div className="lesson-audio">
            <audio ref={audioRef} controls src={lesson.audio}>
              Your browser does not support the audio element.
            </audio>
          </div>

          <div className="speed-control">
            <label>Playback Speed:</label>
            <div className="speed-buttons">
              {speedOptions.map((speed) => (
                <button
                  key={speed}
                  className={`speed-btn ${playbackRate === speed ? 'active' : ''}`}
                  onClick={() => handleSpeedChange(speed)}
                >
                  {speed}x
                </button>
              ))}
            </div>
          </div>
        </>
      )}

      <div className="lesson-transcript">
        <h2>Transcript</h2>
        <p>{lesson.transcript}</p>
      </div>

      {!isEnglish && (
        <div className="tts-section">
          <button 
            onClick={generateAudio} 
            className="tts-generate-btn"
            disabled={isGeneratingAudio}
          >
            {isGeneratingAudio ? 'Generating...' : 'üéµ Generate & Play Audio'}
          </button>
          {generatedAudioUrl && (
            <div className="tts-audio-player">
              <audio ref={ttsAudioRef} controls src={generatedAudioUrl}>
                Your browser does not support the audio element.
              </audio>
            </div>
          )}
        </div>
      )}

      {isEnglish && lesson.quiz && lesson.quiz.length > 0 && (
        <div className="lesson-quiz">
          <h2>Quiz</h2>
          {lesson.quiz.map((quizItem, index) => (
            <div key={index} className="quiz-item">
              <h3 className="quiz-question">
                {index + 1}. {quizItem.question}
              </h3>
              <div className="quiz-options">
                {quizItem.options.map((option, optIndex) => {
                  const isSelected = quizAnswers[index] === option;
                  const isCorrect = option === quizItem.answer;
                  const showFeedback = quizAnswers[index] !== undefined;
                  let optionClass = 'quiz-option-btn';

                  if (showFeedback) {
                    if (isCorrect) {
                      optionClass += ' correct';
                    } else if (isSelected && !isCorrect) {
                      optionClass += ' incorrect';
                    }
                  }

                  return (
                    <button
                      key={optIndex}
                      className={optionClass}
                      onClick={() => handleQuizAnswer(index, option)}
                      disabled={showFeedback}
                    >
                      {option}
                      {showFeedback && isCorrect && ' ‚úì'}
                      {showFeedback && isSelected && !isCorrect && ' ‚úó'}
                    </button>
                  );
                })}
              </div>
              {quizFeedback[index] !== undefined && (
                <div className={`quiz-feedback ${quizFeedback[index] ? 'correct' : 'incorrect'}`}>
                  {quizFeedback[index]
                    ? '‚úì Correct!'
                    : `‚úó Incorrect. The correct answer is: ${quizItem.answer}`}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      <Link 
        to={`/listening-practice?lang=${encodeURIComponent(selectedLanguage)}&code=${encodeURIComponent(languageCode)}`} 
        className="lesson-back-link"
      >
        ‚Üê Back to all listening lessons
      </Link>
    </div>
  );
};

export default ListeningLesson;
