import React, { useEffect, useState } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { getListeningLessons } from '../utils/api';
import ToggleButton from '../components/ToggleButton';
import TTSInputBox from '../components/TTSInputBox';
import SummaryBox from '../components/SummaryBox';
import VocabularyBox from '../components/VocabularyBox';
import './ListeningPractice.css';

const speakText = (text, languageCode) => {
  if (!window.speechSynthesis) {
    alert('SpeechSynthesis API not supported in this browser.');
    return;
  }
  const utter = new SpeechSynthesisUtterance(text);
  utter.lang = languageCode || 'en-US';
  utter.rate = 1;
  utter.pitch = 1;
  window.speechSynthesis.cancel();
  window.speechSynthesis.speak(utter);
};

const ListeningPractice = () => {
  const [searchParams] = useSearchParams();
  const selectedLanguage = searchParams.get('lang') || 'English';
  const languageCode = searchParams.get('code') || 'en-US';

  const [lessons, setLessons] = useState([]);
  const [filteredLessons, setFilteredLessons] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [ttsText, setTtsText] = useState('');
  const [isTTSMode, setIsTTSMode] = useState(languageCode !== 'en-US');
  const [speaking, setSpeaking] = useState(false);
  const [summary, setSummary] = useState('');
  const [vocabulary, setVocabulary] = useState([]);

  const isEnglish = languageCode === 'en-US';

  const categories = ['All', 'Story', 'Conversation', 'Podcast'];

  const placeholder = 'Type or paste text to convert into audio...';

  const generateSummary = (text) => {
    if (!text.trim()) return '';
    const sentences = text
      .split(/[.!?]/)
      .map((s) => s.trim())
      .filter(Boolean);
    const selected = sentences.slice(0, 3);
    if (selected.length === 0) return '';
    const summaryText = selected.join('. ');
    return `${summaryText}${summaryText.endsWith('.') ? '' : '.'}`;
  };

  const extractVocabulary = (text) => {
    const words = text
      .toLowerCase()
      .split(/[^a-zA-Z]/)
      .map((w) => w.trim())
      .filter((w) => w.length > 4);
    const stopWords = new Set(['their', 'there', 'about', 'which', 'would', 'could', 'should', 'these', 'those', 'doing', 'being', 'through', 'other']);
    const filtered = words.filter((w) => !stopWords.has(w));
    const unique = Array.from(new Set(filtered));
    return unique.slice(0, 10);
  };

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const data = await getListeningLessons();
        setLessons(data);
        setFilteredLessons(data);
      } catch (err) {
        setError('Unable to load listening lessons. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    if (isEnglish) {
      fetchLessons();
    } else {
      setLessons([]);
      setFilteredLessons([]);
      setError('');
      setLoading(false);
    }
  }, [isEnglish]);

  useEffect(() => {
    setIsTTSMode(languageCode !== 'en-US');
    setTtsText('');
    setSpeaking(false);
    setSummary('');
    setVocabulary([]);
  }, [languageCode]);

  useEffect(() => {
    if (selectedCategory === 'All') {
      setFilteredLessons(lessons);
    } else {
      setFilteredLessons(lessons.filter(lesson => lesson.category === selectedCategory));
    }
  }, [selectedCategory, lessons]);

  const handleSpeak = () => {
    if (!ttsText.trim()) {
      return;
    }
    setSpeaking(true);
    speakText(ttsText, languageCode);
    setSummary(generateSummary(ttsText));
    setVocabulary(extractVocabulary(ttsText));
  };

  const renderTTSSection = () => (
    <div className={`tts-section ${speaking ? 'tts-section-active' : ''}`}>
      <TTSInputBox
        value={ttsText}
        onChange={setTtsText}
        onSubmit={handleSpeak}
        placeholder={placeholder}
      />
      <SummaryBox summary={summary} />
      <VocabularyBox words={vocabulary} />
    </div>
  );

  if (loading) {
    return <div className="listening-loading">Loading listening lessons...</div>;
  }

  if (error) {
    return <div className="listening-error">{error}</div>;
  }

  return (
    <div className="listening-page">
      <div className="listening-header">
        <h1>Listening Practice</h1>
        <p>
          {isEnglish 
            ? 'Select a lesson to practice your listening skills or switch to Text to Speech mode.'
            : `Speak custom text in ${selectedLanguage} using your browser.`}
        </p>
        <p className="language-badge">Language: <strong>{selectedLanguage}</strong></p>
      </div>

      {isEnglish && (
        <div className="tts-toggle">
          {!isTTSMode && (
            <ToggleButton active={false} onClick={() => setIsTTSMode(true)}>
              Text to Speech Mode
            </ToggleButton>
          )}
          {isTTSMode && (
            <ToggleButton active onClick={() => setIsTTSMode(false)}>
              Back to Lessons
            </ToggleButton>
          )}
        </div>
      )}

      {isEnglish && !isTTSMode && (
        <div className="lessons-wrapper">
          <div className="category-filters">
            {categories.map((category) => (
              <button
                key={category}
                className={`category-filter-btn ${selectedCategory === category ? 'active' : ''}`}
                onClick={() => setSelectedCategory(category)}
              >
                {category}
              </button>
            ))}
          </div>

          <div className="listening-list grid-list fade-in">
            {filteredLessons.map((lesson) => (
              <div key={lesson._id} className="listening-card">
                <div>
                  <h3>{lesson.title}</h3>
                  <p>
                    {lesson.category && <span className="lesson-category">{lesson.category}</span>}
                    {lesson.category && ' • '}
                    {lesson.level || 'Beginner'} • {lesson.duration ? `${lesson.duration}s` : 'Duration TBD'}
                  </p>
                </div>
                <div className="listening-actions">
                  {isEnglish && (
                    <audio controls src={lesson.audio}>
                      Your browser does not support the audio element.
                    </audio>
                  )}
                  <Link 
                    to={`/listening-practice/${lesson._id}?lang=${encodeURIComponent(selectedLanguage)}&code=${encodeURIComponent(languageCode)}`} 
                    className="listening-link"
                  >
                    View Details
                  </Link>
                </div>
              </div>
            ))}
            {filteredLessons.length === 0 && (
              <div className="listening-empty">No listening lessons available in this category.</div>
            )}
          </div>
        </div>
      )}

      {(!isEnglish || isTTSMode) && (
        <div className="tts-wrapper fade-in">
          {renderTTSSection()}
        </div>
      )}
    </div>
  );
};

export default ListeningPractice;
